    private void jButton5ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton5ActionPerformed
        ModeloMedico mod = new ModeloMedico();
        int codigo = Integer.parseInt(jTextFieldCodigo.getText());
        String nome = jTextFieldNome.getText();
        int crm = Integer.parseInt(jFormattedTextFieldCRM.getText());
        String Especialidade = jComboBoxEspecialidade.getActionCommand();
        
        mod.setCodigo(codigo);
        mod.setNome(nome);
        mod.setCrm(crm);
        mod.setEspecialidade(Especialidade);
        control.salvar(mod);
        JOptionPane.showMessageDialog(null, "Codigo: "+codigo+ "\nNome: "+nome+
            "\nCRM: " +crm);
    }//GEN-LAST:event_jButton5ActionPerformed

